<script context="module"></script>

<script>export let text;
function mitosis_styling(node, vars) {
    Object.entries(vars || {}).forEach(([p, v]) => {
        if (p.startsWith("--")) {
            node.style.setProperty(p, v);
        }
        else {
            node.style[p] = v;
        }
    });
}
</script>

<span
  use:mitosis_styling={{
    outline: "none",
  }}
  class="builder-text">{@html text}</span
>